INSERT INTO `posts` VALUES ('14', '3', '0', '5', '5', '1', '7', '0', 'Article', '爱恩学院英语晚会完美演出', '爱恩学院英语晚会完美演出', '<div style=\"line-height: 150%; text-indent: 24pt\">\r\n	<span style=\"line-height: 150%; font-size: 12pt\">4</span><span style=\"line-height: 150%; font-size: 12pt\">月</span><span style=\"line-height: 150%; font-size: 12pt\">27</span><span style=\"line-height: 150%; font-size: 12pt\">日下午两点整，第二届&ldquo;</span><span style=\"line-height: 150%; font-size: 12pt\">English Talent Concert</span><span style=\"line-height: 150%; font-size: 12pt\">&rdquo;在中日友好大连人才培训中心五楼会议室准时开始。</span></div>\r\n<div style=\"line-height: 150%; text-indent: 24pt\">\r\n	<span style=\"line-height: 150%; font-size: 12pt\">本次晚会由大连交通大学爱恩国际学院主办，协助单位有：中日友好大连人才培训中心、学生工作部、教学部、专业教师办公室、后勤部、学生会和院办公室。参加演出的有</span><span style=\"line-height: 150%; font-size: 12pt\">09</span><span style=\"line-height: 150%; font-size: 12pt\">级会计</span><span style=\"line-height: 150%; font-size: 12pt\">3</span><span style=\"line-height: 150%; font-size: 12pt\">班和</span><span style=\"line-height: 150%; font-size: 12pt\">10</span><span style=\"line-height: 150%; font-size: 12pt\">级会计全体同学。</span></div>\r\n<div style=\"line-height: 150%; text-indent: 24pt\">\r\n	</div>\r\n<div style=\"line-height: 150%; text-indent: 24pt\">\r\n	<span style=\"line-height: 150%; font-size: 12pt\"><img alt=\"\" src=\"/jiaoda_community/app/webroot/media/static/images/20120224085127.jpg\" style=\"width: 500px; height: 400px\" /></span></div>\r\n<div style=\"line-height: 150%; text-indent: 24pt\">\r\n	<span style=\"line-height: 150%; font-size: 12pt\"><img alt=\"\" src=\"/jiaoda_community/app/webroot/media/static/images/20120224085144.jpg\" style=\"width: 500px; height: 400px\" /></span></div>\r\n', '/jiaoda_community/app/webroot/media/static/images/20120224085127.jpg|/jiaoda_community/app/webroot/media/static/images/20120224085144.jpg', '/jiaoda_community/app/webroot/media/static/images/20120224085127.jpg', '2012-03-05 00:00:00', '0000-00-00 00:00:00', '2012-02-24 15:52:36', '2012-03-13 16:57:03', '2012-05-19 09:25:28');
INSERT INTO `posts` VALUES ('15', '3', '0', '5', '4', '1', '10', '0', 'Article', '大连市大学生“建诚信校园，迎和谐全运”活动在我校启动', '大连市大学生“建诚信校园，迎和谐全运”活动在我校启动', '<p>\r\n	<font size=\"1\">11月30日下午1点30分，大连市大学生&ldquo;建诚信校园，迎和谐全运&rdquo;启动仪式在我校举行，大连市政府副秘书长王延辉同志到会并致辞。丹东市政府、大连市委宣传部、市委高校工委、团市委、市文广局、市体育局、市信用办等单位的领导参加了当天的启动仪式，我校党委副书记刘玺明同志代表在连高校领导出席了启动仪式并在主席台就坐。</font></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size: x-small\"><img alt=\"\" height=\"605\" src=\"http://tw.djtu.edu.cn//userfiles/2011120108280570554.jpg\" style=\"width: 661px; height: 483px\" width=\"811\" /><br />\r\n	 </span></p>\r\n<p>\r\n	<span style=\"font-size: x-small\"> 首先，我校学生会主席朱云峰同学代表青年学子向大连市、丹东市青年发出倡议，号召高校青年积极响应&ldquo;建诚信校园，迎和谐全运&rdquo;活动号召，踊跃参与各院校举办的诚信系列活动，营造健康向上的校园诚信氛围。接下来共青团大连市委副书记张延松同志为高校授旗，我校团委书记吴毅同志代表大连、丹东两地高校接旗。随后，大连市、丹东市领导共同开启启动球，大连市大学生&ldquo;建诚信校园，迎和谐全运&rdquo;活动正式启动。</span></p>\r\n<p>\r\n	<span style=\"font-size: x-small\"><img alt=\"\" height=\"606\" src=\"http://tw.djtu.edu.cn//userfiles/2011120108291070554.jpg\" style=\"width: 663px; height: 374px\" width=\"809\" /></span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size: x-small\"> 当代大学生是社会的特殊群体，是诚信道德和守信行为的示范者和传播者，同时也是国家未来的建设者，大学生的诚信状况将直接关系到我国社会主义现代化建设的顺利进行。所以，树道德之新风，立诚信之根基，是当代大学生实现人生价值的重要内容，是每一位大学生义不容辞的责任。</span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size: x-small\"><img alt=\"\" height=\"612\" src=\"http://tw.djtu.edu.cn//userfiles/2011120108293770554.jpg\" style=\"width: 689px; height: 600px\" width=\"814\" /></span></p>\r\n<p>\r\n	<span style=\"font-size: x-small\"> 会议最后，在连部分高校学生代表在&ldquo;建诚信校园，迎和谐全运&rdquo;长卷上签名。在一片热烈的掌声中，启动仪式圆满地落下帷幕。</span></p>\r\n', 'http://tw.djtu.edu.cn//userfiles/2011120108280570554.jpg|http://tw.djtu.edu.cn//userfiles/2011120108291070554.jpg|http://tw.djtu.edu.cn//userfiles/2011120108293770554.jpg', 'http://tw.djtu.edu.cn//userfiles/2011120108291070554.jpg', '2012-03-07 00:00:00', '0000-00-00 00:00:00', '2012-03-01 18:55:02', '2012-03-08 20:52:18', '2012-05-19 09:25:28');
