INSERT INTO `posts` VALUES ('89', '3', '0', '5', '2', '1', '1', '0', 'Article', '校团委岗位职责1', '校团委岗位职责1', '<p>\r\n	</p>\r\n<p>\r\n	<span style=\"font-size: 16px\"><img src=\"/jiaoda_community/app/webroot/media/static/images/20120314115643.jpg\" style=\"width: 252px; float: left; height: 189px; margin-left: 12px; margin-right: 12px\" />★吴毅（团委书记）</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    1、根据校党委和上级团组织的工作部署，规划并负责团委全面工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    2、指导基层团组织开展工作、加强建设，组织青年、引导青年、服务青年。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    3、指导学生会秘书长和社团联合会秘书长的工作，及时发现、解决学生会和社团联合会工作中的问题。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    4、兼任大学生素质教育中心副主任，协助主任做好大学生素质教育中心工作，组织做好大学生素质教育。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    5、担任校级学生组织临时党支部书记，负责大学生政治理论研究会的指导工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    6、协调相关部门做好与学生有关的工作，完成学校及上级团组织交办的其他工作。</span></p>\r\n<p>\r\n	</p>\r\n<p>\r\n	<span style=\"font-size: 16px\"><img align=\"left\" height=\"189\" hspace=\"12\" src=\"file:///C:/DOCUME~1/ADMINI~1/LOCALS~1/Temp/msohtml1/01/clip_image004.jpg\" width=\"252\" />★孙浩（团委副书记）</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    1、了解团员青年的思想状况，协助书记分管旅顺口校区的共青团工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    2、负责全校共青团促进学风建设和促进青年就业创业的相关工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">   3、负责全校大学生志愿服务工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    4、负责旅顺口校区团委所属场地的安全卫生检查、管理和使用工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    5、负责旅顺口校区大型活动的策划和组织，协调解决旅顺口校区团学活动中出现的问题。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    6、兼任旅顺口校区学生作部副部长，配合部长完成相关工作。</span></p>\r\n<p>\r\n	</p>\r\n<p>\r\n	<span style=\"font-size: 16px\"><img src=\"/jiaoda_community/app/webroot/media/static/images/20120314115702.JPG\" style=\"width: 252px; float: left; height: 189px; margin-left: 12px; margin-right: 12px\" />★刘灿娟（团委副书记）</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    1、了解团员青年的思想状况，协助书记分管沙河口校区的共青团工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    2、协助书记做好全校团的基层组织建设工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    3、兼任团校校长，负责全校团干部队伍建设和团员队伍建设。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    4、负责学生的勤工俭学工作和来访的接待工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    5、负责沙河口校区团委所属场地的安全卫生检查、管理和使用工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    6、负责沙河口校区大型活动的策划和组织，协调解决沙河口校区团学活动中出现的问题。</span></p>\r\n<p>\r\n	</p>\r\n<p>\r\n	<span style=\"font-size: 16px\"><img src=\"/jiaoda_community/app/webroot/media/static/images/20120314115726.jpg\" style=\"width: 252px; float: left; height: 189px; margin-left: 12px; margin-right: 12px\" />★赵春越（团委干事）</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    1、担任社团联合会秘书长，负责指导学校两级社团开展活动。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    2、配合学生会秘书长指导好旅顺口校区的学生会工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    3、负责全校大学生素质拓展计划的实施和认证工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    4、负责全校的社会实践工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    5、负责旅顺口校区团的宣传工作和广播台的指导工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    6、负责旅顺口校区办公室的日常工作（来信处理、库房管理、文件和报刊管理、办公用品管理等）。</span></p>\r\n<p>\r\n	</p>\r\n<p>\r\n	<span style=\"font-size: 16px\"><img src=\"/jiaoda_community/app/webroot/media/static/images/20120314115738.JPG\" style=\"width: 252px; float: left; height: 189px; margin-left: 12px; margin-right: 12px\" />★汪舰（团委干事）</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    1、担任学生会秘书长，负责指导学校两级学生会开展工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    2、配合社团联合会秘书长指导好沙河口校区的社团活动。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    3、负责全校共青团的宣传工作、团属阵地的建设工作、团委网站的管理和维护工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    4、负责团员证补办和团费的收缴、上交、管理工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    5、负责演出服装的管理、维护和国旗班的指导工作。</span></p>\r\n<p>\r\n	<span style=\"font-size: 16px\">    6、负责沙河口校区办公室的日常工作（来信处理、库房管理、文件和报刊管理、办公用品管理等）。</span></p>\r\n', '', '', '2012-03-19 00:00:00', '0000-00-00 00:00:00', '2012-03-19 21:42:25', '2012-03-19 21:42:51', '2012-05-19 09:25:28');
INSERT INTO `posts` VALUES ('90', '3', '0', '5', '4', '1', '208', '0', 'Article', '党委书记葛继平与学生干部共话学生组织建设发展', '党委书记葛继平与学生干部共话学生组织建设发展', '<p>\r\n	</p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"font-family:宋体;\"> 3月23日下午，校党委书记葛继平教授来到校团委亲切看望学生会、社团联合会的学生干部，并在社团联合会办公室与学生干部代表座谈交流，共话学生组织的建设发展，受到学生干部们的热烈欢迎。</span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"font-family:宋体;\">  葛书记关心的询问学生组织的发展情况、素质教育中心指导教师与学生社团的对接情况、学生组织的经费情况、运行过程中遇到的困难，学生会、社团联合会的学生干部从学生组织的机构设置、工作开展、社团建设、个人发展等多个方面向葛书记作了情况汇报。葛书记还应社团联合会同学们的请求，介绍了国外学生社团的运营模式，带给同学们深刻的启发和思考。现场气氛轻松、和谐，不时响起热烈的掌声。</span></span></p>\r\n<p style=\"text-align: center; \">\r\n	<br />\r\n	<span style=\"font-size:14px;\"><span style=\"font-family:宋体;\"><img alt=\"\" src=\"/media/static/images/20120324055149.jpg\" style=\"width: 600px; height: 349px; \" /></span></span></p>\r\n<p style=\"text-align: left; \">\r\n	<br />\r\n	<span style=\"font-size:14px;\"><span style=\"font-family:宋体;\"> 葛书记对我校学生组织的工作给予肯定，他语重心长的说，学生干部是我校学生工作队伍中一支重要的力量，是学校和老师联系广大同学的桥梁纽带，在服务学校、服务师生、服务社会方面发挥了不容忽视的作用，学生干部也通过自身的工作，锻炼了能力，提高了综合素质。他希望同学们珍惜担任学生干部的宝贵机会，踏实工作，锤炼经历，以积极的心态创造性地开展工作；希望同学们正确看待我校一校两区格局下学生组织的特殊性，通过信息平台、网络平台等多种方式加强联系，保持信息畅通，促进两校区的融合，根据校区的特点，开展丰富多彩的校园文化活动；希望学生社团充分利用学校资源，开展活动吸引同学、凝聚同学，进一步发挥自我教育、自我管理、自我服务的功能。</span></span></p>\r\n<p style=\"text-align: center; \">\r\n	<br />\r\n	<span style=\"font-size:14px;\"><span style=\"font-family:宋体;\"><img alt=\"\" src=\"/media/static/images/20120324055228.jpg\" style=\"width: 600px; height: 338px; \" /></span></span></p>\r\n<p>\r\n	<br />\r\n	<span style=\"font-size:14px;\"><span style=\"font-family:宋体;\">   葛书记表示将帮助学生组织解决遇到的问题，促进学生组织建设，并和同学们约定再来看望大家。在场的学生干部们纷纷表示，听了葛书记的讲话振奋人心，一定要把葛书记的关怀和教育传达给周围的同学，在今后的学习和工作中会更加努力，不辜负领导的期望，为学校的发展建设贡献自己应有的力量。</span></span></p>\r\n', '/media/static/images/20120324055149.jpg|/media/static/images/20120324055228.jpg', '/media/static/images/20120324055149.jpg', '2012-03-24 00:00:00', '2012-03-26 14:09:25', '2012-03-24 13:52:43', '0000-00-00 00:00:00', '2012-05-19 09:25:28');
